    <h1 class=\"text-center\">Nos articles</h1>

    < class=\"row\">
        {% for item in items %}
            <div class=\"col-md-4\">
                <div class=\"product text-center\">
                    <img src=\"uploads/item_images/{{ item.image }}\" alt=\"{{ item.alt }}\" class=\"img-fluid bd-placeholder-img rounded-circle\">
                    <h5 class=\"product-title\">{{ item.name }}</h5>
                    <span class=\"product-price\">{{ item.price }}</span>
                    <p class=\"product-description text-align-justify-center\">{{ item.description }}</p>
                </div>
            </div>
        {% endfor %}
    </div>

{% endblock %}
